function critSound() {
  let critSound = new Audio('https://fleury14.github.io/cyo/sound/persona5-crit.wav');
  critSound.play();
	jokerBox.classList.remove('hide-joker');
  setTimeout(function() {jokerBox.classList.add('done-joker')}, 1000);
  setTimeout(function() {jokerBox.classList.add('no-transition')}, 1020);
  setTimeout(function() {jokerBox.classList.add('hide-joker')}, 1040);
  setTimeout(function() {jokerBox.classList.remove('done-joker')}, 1060);
  setTimeout(function() {jokerBox.classList.remove('no-transition')}, 1080);
}

var userChoice;
document.getElementById("rock").addEventListener("click", function(){
    userChoice = 1;
    rock.style.color = 'red';
    paper.style.color = 'white';
    scissors.style.color = 'white';
    rock.style.outline = 'none';
    var randomNumber = (Math.ceil(Math.random() * 3));
    if (userChoice == 1 && randomNumber == 1) {
    	bothRock();
			badsound();

    }
    if (userChoice == 1 && randomNumber == 2) {
    	beatByPaper();
    }
    if (userChoice == 1 && randomNumber == 3) {
    	rockBeatsScissors();
			critSound();
    }
    if (userChoice == 2 && randomNumber == 1) {
    	paperBeatsRock();
			critSound();
    }
    if (userChoice == 2 && randomNumber == 2) {
    	bothPaper();
    }
    if (userChoice == 2 && randomNumber == 3) {
    	beatByScissors();
    }
    if (userChoice == 3 && randomNumber == 1) {
    	beatByRock();
    }
    if (userChoice == 3 && randomNumber == 2) {
    	scissorsBeatsPaper();
			critSound();
    }
    if (userChoice == 3 && randomNumber == 3) {
    	bothScissors();
    }
});

